INSERT INTO behindthescreen.games (name, type, description, version, release_date) VALUES ('Donjon & Dragons', 'Mediéval Fantastique', 'Plus connus des jeurs de rôle', 5, '2014-07-03');
INSERT INTO behindthescreen.games (name, type, description, version, release_date) VALUES ('L''Appel de Cthulhu', 'Horreur/Enquête', 'À la frontière de la folie des hommes, L’Appel de Cthulhu plonge les joueurs dans les écrits et les mondes torturés de l’auteur américain Howard Phillips Lovecraft : le cosmos est régi par des entités élevées au rang de dieux, conscience pure ou monstruosité maligne, dont les intentions nous échappent. À la recherche de secrets perdus et confronté à l’innommable, basculerez-vous dans la folie ou parviendrez-vous à repousser les horreurs sans nom ? Voici le genre d’enjeux auxquels sont confrontés les courageux investigateurs !', 7, '2016-05-16');
INSERT INTO behindthescreen.games (name, type, description, version, release_date) VALUES ('Pathfinder', 'Mediéval Fantastique', 'Ce jeu de rôle se fonde sur les règles de Donjons et Dragons édition 3.5 qu''il enrichit et améliore. Il est largement compatible avec Donjons et Dragons 3.5, et constitue pour ses fans une alternative à la quatrième édition de Donjons et Dragons, laquelle n''est pas compatible avec l''édition précédente.', 2, '2019-08-01');
